function constructAlarmAddressSpace(server, addressSpace, eventObjects, done) {
  // server = the created node-opcua server
  // addressSpace = address space of the node-opcua server
  // eventObjects = add event variables here to hold them in memory from this script

  // internal sandbox objects are:
  // node = the compact server node,
  // coreServer = core compact server object for debug and access to NodeOPCUA
  // this.sandboxNodeContext = node context node-red
  // this.sandboxFlowContext = flow context node-red
  // this.sandboxGlobalContext = global context node-red
  // this.sandboxEnv = env variables
  // timeout and interval functions as expected from nodejs


  const opcua = coreServer.choreCompact.opcua;
  const LocalizedText = opcua.LocalizedText;
  const namespace = addressSpace.getOwnNamespace();

  const Variant = opcua.Variant;
  const DataType = opcua.DataType;
  const DataValue = opcua.DataValue;

  var flexServerInternals = this;


  const rootFolder = addressSpace.findNode("RootFolder");

  this.sandboxFlowContext.set("Humidity", false);
  this.sandboxFlowContext.set("Temperature", false);
  this.sandboxFlowContext.set("humiditycriticalvl", false);
  this.sandboxFlowContext.set("tempecriticalvl", false);
  this.sandboxFlowContext.set("light_on", false);

  this.sandboxFlowContext.set("access", false);
  this.sandboxFlowContext.set("illuminance", false);


  const sensor_Data = namespace.addFolder(rootFolder.objects, {
    "browseName": "Sensor_data"
  });
  const access_control = namespace.addFolder(rootFolder.objects, {
    "browseName": "access_control"
  });
  const access = namespace.addFolder(access_control, {
    "browseName": "access_event"
  });
  const temperature = namespace.addFolder(sensor_Data, {
    "browseName": "temperature"
  });
  const humidity = namespace.addFolder(sensor_Data, {
    "browseName": "humidity"
  });
  const temperature_float = namespace.addFolder(temperature, {
    "browseName": "Float_C"
  });
  const humidity_float = namespace.addFolder(humidity, {
    "browseName": "humidity_%"
  });
  const tempCritical = namespace.addFolder(sensor_Data, {
    "browseName": "Temp_critical"
  });
  const humidityCritical = namespace.addFolder(sensor_Data, {
    "browseName": "Humidity_critical"
  });
   const light_on = namespace.addFolder(sensor_Data, {
    "browseName": "light_on"
  });
  const illuminance = namespace.addFolder(sensor_Data, {
  "browseName": "illuminance"
  });
  const temperature_var = namespace.addVariable({
    "organizedBy": temperature_float,
    "browseName": "temperature",
    "nodeId": "ns=1;s=Temperature_value",
    "dataType": "Double",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("Temperature")
        });
      },
      "set": function (Variant) {
        flexServerInternals.sandboxFlowContext.set(
          "Temperature",
          Variant.value
        );
        return opcua.StatusCodes.Good;
      }
    }
  });
  const humidity_var = namespace.addVariable({
    "organizedBy": humidity_float,
    "browseName": "Humidity",
    "nodeId": "ns=1;s=Humidity_value",
    "dataType": "Double",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("Humidity")
        });
      },
      "set": function (Variant) {
        flexServerInternals.sandboxFlowContext.set(
          "Humidity",
          Variant.value
        );
        return opcua.StatusCodes.Good;
      }
    }
  });
    const temperatureCritical = namespace.addVariable({
    "organizedBy": tempCritical,
    "browseName": "critical_temperature",
    "nodeId": "ns=1;s=Temp_critical_value",
    "dataType": "Boolean",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Boolean,
          "value": flexServerInternals.sandboxFlowContext.get("tempecriticalvl")
        });
      },
      "set": function (variant) {
        flexServerInternals.sandboxFlowContext.set(
          "tempecriticalvl",
          variant.value
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

  const humidityCrit = namespace.addVariable({
    "organizedBy": humidityCritical,
    "browseName": "critical_humidity",
    "nodeId": "ns=1;s=Humidity_critical_value",
    "dataType": "Boolean",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Boolean,
          "value": flexServerInternals.sandboxFlowContext.get("humiditycriticalvl")
        });
      },
      "set": function (variant) {
        flexServerInternals.sandboxFlowContext.set(
          "humiditycriticalvl",
          variant.value
        );
        return opcua.StatusCodes.Good;
      }
    }
  });
  const light_on_event = namespace.addVariable({
    "organizedBy": light_on,
    "browseName": "light_is_on",
    "nodeId": "ns=1;s=light_is_on",
    "dataType": "Boolean",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Boolean,
          "value": flexServerInternals.sandboxFlowContext.get("light_on")
        });
      },
      "set": function (variant) {
        flexServerInternals.sandboxFlowContext.set(
          "light_on",
          variant.value
        );
        return opcua.StatusCodes.Good;
      }
    }
  });
  const accessevent= namespace.addVariable({
    "organizedBy": access_control,
    "browseName": "Value",
    "nodeId": "ns=1;s=accessevent",
    "dataType": "Boolean",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Boolean,
          "value": flexServerInternals.sandboxFlowContext.get("access")
        });
      },
      "set": function (variant) {
        flexServerInternals.sandboxFlowContext.set(
          "access",
          variant.value
        );
        return opcua.StatusCodes.Good;
      }
    }
  });
const illuminance_var = namespace.addVariable({
    "organizedBy": illuminance,
    "browseName": "illuminance",
    "nodeId": "ns=1;s=illuminance_value",
    "dataType": "Double",
    "value": {
      "get": function () {
        return new Variant({
          "dataType": DataType.Double,
          "value": flexServerInternals.sandboxFlowContext.get("lux")
        });
      },
      "set": function (Variant) {
        flexServerInternals.sandboxFlowContext.set(
          "lux",
          Variant.value
        );
        return opcua.StatusCodes.Good;
      }
    }
  });

  done();
}

